package problems.problem0076

class Solution {
  fun minWindow(s: String, t: String): String {
    val tMap = getFrequencyMap(t)

    var first = 0
    var second = 0
    var currentShortest: String? = null
    val windowMap = mutableMapOf<Char, Int>()
    while (true) {
      // first, expand window until we have all characters
      while (!windowMap.isSubset(tMap) && second < s.length) {
        if (tMap.containsKey(s[second])) {
          windowMap.increment(s[second])
        }
        second++
      }

      if (!windowMap.isSubset(tMap)) {
        break
      }

      // contract from the front
      while (windowMap.isSubset(tMap) && first < s.length) {
        windowMap.decrement(s[first])
        first++
      }

      val currentSubstring = s.substring(first - 1, second)
      if (currentShortest == null || currentShortest.length > currentSubstring.length) {
        currentShortest = currentSubstring
      }
    }


    return currentShortest ?: ""
  }

  private fun Map<Char, Int>.isSubset(other: Map<Char, Int>): Boolean {
    if (this.keys != other.keys) {
      return false
    }
    for (entry in this.entries) {
      if (other[entry.key]!! > entry.value) {
        return false
      }
    }
    return true
  }

  private fun getFrequencyMap(s: String): MutableMap<Char, Int> {
    val map = mutableMapOf<Char, Int>()
    for (c in s.toCharArray()) {
      map.merge(c, 1) {cur, acc -> cur + acc}
    }
    return map
  }

  private fun MutableMap<Char, Int>.decrement(c: Char) {
    val value = this[c] ?: return
    if (value == 1) {
      this.remove(c)
    }
    else {
      this[c] = value - 1
    }
  }

  private fun MutableMap<Char, Int>.increment(c: Char) {
    val value = this[c]
    if (value == null) {
      this[c] = 1
    }
    else {
      this[c] = value + 1
    }
  }

}

data class Pointer(val start: Int, val end: Int) {
  fun getSize(): Int = end - start
}

fun main(args: Array<String>) {
  println(Solution().minWindow("a", "a"))
  println(Solution().minWindow("a", "aa"))
  println(Solution().minWindow("ADOBECODEBANC", "ABC"))
  /*println(Solution().minWindow(
    "",
    ""))*/


}